import React from "react"
import youtube from "../apis/youtube"

import SearchBar from "./SearchBar"

class App extends React.Component {
    onInputSubmit = (searchInput) => {
        //(2)searchBardan alınan input bilgisi ile işlem yapılıyor
        youtube.get('/search/', {
            params: {
                q: searchInput
            }

        })
    }
    render() {
        return (
            <main className="ui container" style={{ margin: '30px 20px' }}>
                <SearchBar onFormSubmit={this.onInputSubmit} />
            </main >
        )
    }
}

export default App

// render içindeki searchBar onsubmitform callback ile parent childdan bilgi alıyor
